import React, { useState } from "react";
import Layout from "../../components/Layouts";
import {
  Container,
  Row,
  Col,
  Button,
  Table,
  Carousel,
  ButtonGroup,
} from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Input from "../../components/UI/Input";
import { addProduct, updateProduct, deleteProduct } from "../../actions";
import CurrencyFormat from "react-currency-format";
import NewModal from "../../components/UI/Modal";
import { generatePublicUrl } from "../../urlConfig";
import "./style.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Products(props) {
  const [productName, setProductName] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productQty, setProductQty] = useState("");
  const [productDescription, setProductDescription] = useState("");
  const [productOffer, setProductOffer] = useState("");
  const [productCategory, setProductCategory] = useState("");
  const [productImage, setProductImage] = useState([]);
  const [productDetailsModal, setProductDetailsModal] = useState(false);
  const [productDetails, setProductDetails] = useState(null);
  const [deleteProductModal, setDeleteProductModal] = useState(false);
  const [currentProduct, setCurrentProduct] = useState({});
  const [updateProductModal, setUpdateProductModal] = useState(false);

  const [productIdUpdate, setProductIdUpdate] = useState("");
  const [productNameUpdate, setProductNameUpdate] = useState("");
  const [productPriceUpdate, setProductPriceUpdate] = useState("");
  const [productQtyUpdate, setProductQtyUpdate] = useState("");
  const [productDescriptionUpdate, setProductDescriptionUpdate] = useState("");
  const [productOfferUpdate, setProductOfferUpdate] = useState("");
  const [productCategoryUpdate, setProductCategoryUpdate] = useState({});
  const [productImageUpdate, setProductImageUpdate] = useState([]);

  const category = useSelector((state) => state.category);
  const product = useSelector((state) => state.product);

  const dispatch = useDispatch();

  /*  useEffect(() => {
    dispatch(getAllCategory());
  }, []); */

  const [show, setShow] = useState(false);

  //product is loading display loading spinner
  if (product.loading) {
    return <div className="spinner-border text-primary" role="status"></div>;
  }

  const addNewProduct = () => {
    const form = new FormData();

    //validations of data
    if (productName === "") {
      toast.error("Name can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (productPrice === "") {
      toast.error("Price can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (isNaN(productPrice)) {
      toast.error("Price must be a number!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (productQty === "") {
      toast.error("Quantity can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (isNaN(productQty)) {
      toast.error("Quantity must be a number!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (productDescription === "") {
      toast.error("Description can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (productOffer === "") {
      toast.error("Offer can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (isNaN(productOffer)) {
      toast.error("Offer must be a number!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (!productCategory) {
      toast.error("Category can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (productImage.length < 0) {
      toast.error("Product images can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }

    form.append("name", productName);
    form.append("price", productPrice);
    form.append("offer", productOffer);
    form.append("category", productCategory);
    form.append("quantity", productQty);
    form.append("description", productDescription);

    for (let pic of productImage) {
      form.append("productImages", pic);
    }

    dispatch(addProduct(form));

    setProductName("");
    setProductPrice("");
    setProductOffer("");
    setProductCategory("");
    setProductQty("");
    setProductDescription("");
    setProductImage([]);
    //window.location.reload();
  };

  const handleClose = () => {
    setShow(false);
  };
  const handleShow = () => setShow(true);

  const handleProductImage = (e) => {
    setProductImage([...productImage, e.target.files[0]]);
  };

  //handling selected product image in product updating
  const handleProductImageUpdate = (e) => {
    setProductImageUpdate(e.target.files[0]);
  };

  //updating the state after selecting product data to update
  const updateProductData = (prod) => {
    setUpdateProductModal(true);

    console.log(prod._id);

    //updating state value according to selected Product
    setProductIdUpdate(prod._id);
    setProductNameUpdate(prod.name);
    setProductDescriptionUpdate(prod.description);
    setProductQtyUpdate(prod.quantity);
    setProductOfferUpdate(prod.offer);
    setProductPriceUpdate(prod.price);
    setProductOfferUpdate(prod.offer);
    setProductCategoryUpdate(prod.category);

    prod.productImages.map((picture) => setProductImageUpdate(picture.img));
  };

  //handling the data added to form when updating the Product and passing them to updateProduct() in actions
  const updateProductForm = () => {
    const form = new FormData();

    //validations of data
    if (productNameUpdate === "") {
      toast.error("Name can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (productPriceUpdate === "") {
      toast.error("Price can't be empty!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
      return;
    }
    if (isNaN(productPriceUpdate)) {
      toast.error("Price must be a number!", {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });
