                categoryList={categoryList}
                handleCategoryImage={handleCategoryImage}
            /> }
            { <UpdateCategoriesModal
                show={updateCategoryModal}
                handleClose={() => setUpdateCategoryModal(false)}
                onSubmit={updateCategoriesForm}
